function shutdownMenu(){document.getElementById("shutdownMenu").style.display="block"}function exitShutdown(){document.getElementById("shutdownMenu").style.display="none"}function updateVolume(){realtimeVolume=document.getElementById("volumeControl").value,realtimeVolume>74?document.getElementById("volumeIcon").src="./img/volume/high-volume.png":realtimeVolume>24?document.getElementById("volumeIcon").src="./img/volume/medium-volume.png":realtimeVolume>1?document.getElementById("volumeIcon").src="./img/volume/low-volume.png":realtimeVolume<=1&&(document.getElementById("volumeIcon").src="./img/volume/mute.png")}function leftUpMenu(){"none"==document.getElementById("slidecontainer").style.display?(document.getElementById("slidecontainer").style.display="block",volUp=setInterval(updateVolume,50)):("undifined"!=volUp&&clearInterval(volUp),document.getElementById("slidecontainer").style.display="none"),sliderclick=1}function volumeCursorEscape(){0==sliderclick&&(document.getElementById("slidecontainer").style.display="none"),sliderclick=0}function shutdown(){return setTimeout((function(){window.location="./Shutdown.php"}),100),"Shuting down..."}sliderclick=0;